contracts/BountyEscrow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.25;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title BountyEscrow
 * @notice Manages on-chain escrow for legal bounties while anchoring Constellation DAG proofs for audit trails.
 * @dev Funds are denominated in native currency (ETH on Ethereum testnets). Designed for simple plug-and-play usage.
 */
contract BountyEscrow is Ownable, ReentrancyGuard {
    struct Escrow {
        address funder;
        address payable beneficiary;
        uint256 amount;
        string dagProofHash;
        bool released;
        bool refunded;
        uint256 createdAt;
        uint256 finalizedAt;
    }

    mapping(uint256 bountyId => Escrow) private _escrows;

    event EscrowCreated(
        uint256 indexed bountyId,
        address indexed funder,
        address indexed beneficiary,
        uint256 amount
    );
    event DagProofAnchored(uint256 indexed bountyId, string dagProofHash);
    event EscrowReleased(uint256 indexed bountyId, address indexed beneficiary, uint256 amount);
    event EscrowRefunded(uint256 indexed bountyId, address indexed funder, uint256 amount);

    error EscrowAlreadyExists(uint256 bountyId);
    error EscrowNotFound(uint256 bountyId);
    error NothingToRelease(uint256 bountyId);
    error AlreadyFinalized(uint256 bountyId);

    constructor(address initialOwner) Ownable(initialOwner) {}

    /**
     * @notice Creates and funds an escrow for a bounty. The funds are locked until released or refunded.
     * @param bountyId Off-chain managed bounty identifier.
     * @param beneficiary Address entitled to receive the funds upon successful submission.
     */
    function createEscrow(uint256 bountyId, address payable beneficiary) external payable {
        if (_escrows[bountyId].createdAt != 0) {
            revert EscrowAlreadyExists(bountyId);
        }
        if (msg.value == 0) {
            revert NothingToRelease(bountyId);
        }

        _escrows[bountyId] = Escrow({
            funder: msg.sender,
            beneficiary: beneficiary,
            amount: msg.value,
            dagProofHash: "",
            released: false,
            refunded: false,
            createdAt: block.timestamp,
            finalizedAt: 0
        });

        emit EscrowCreated(bountyId, msg.sender, beneficiary, msg.value);
    }

    /**
     * @notice Anchor the Constellation DAG hash for the reasoning that justifies payout.
     * @dev Only the contract owner (platform operator) can anchor proofs to avoid malicious overwrite.
     */
    function anchorDagProof(uint256 bountyId, string calldata dagProofHash) external onlyOwner {
        Escrow storage escrow = _escrows[bountyId];
        if (escrow.createdAt == 0) {
            revert EscrowNotFound(bountyId);
        }
        if (escrow.released || escrow.refunded) {
            revert AlreadyFinalized(bountyId);
        }

        escrow.dagProofHash = dagProofHash;

        emit DagProofAnchored(bountyId, dagProofHash);
    }

    /**
     * @notice Releases escrowed funds to the beneficiary once proofs have been verified.
     */
    function release(uint256 bountyId) external onlyOwner nonReentrant {
        Escrow storage escrow = _escrows[bountyId];
        if (escrow.createdAt == 0) {
            revert EscrowNotFound(bountyId);
        }
        if (escrow.released || escrow.refunded) {
            revert AlreadyFinalized(bountyId);
        }
        if (escrow.amount == 0) {
            revert NothingToRelease(bountyId);
        }

        uint256 amount = escrow.amount;
        escrow.amount = 0;
        escrow.released = true;
        escrow.finalizedAt = block.timestamp;

        (bool success, ) = escrow.beneficiary.call{value: amount}("");
        require(success, "Transfer failed");

        emit EscrowReleased(bountyId, escrow.beneficiary, amount);
    }

    /**
     * @notice Refunds the funder if the bounty is cancelled.
     */
    function refund(uint256 bountyId) external onlyOwner nonReentrant {
        Escrow storage escrow = _escrows[bountyId];
        if (escrow.createdAt == 0) {
            revert EscrowNotFound(bountyId);
        }
        if (escrow.released || escrow.refunded) {
            revert AlreadyFinalized(bountyId);
        }
        if (escrow.amount == 0) {
            revert NothingToRelease(bountyId);
        }

        uint256 amount = escrow.amount;
        escrow.amount = 0;
        escrow.refunded = true;
        escrow.finalizedAt = block.timestamp;

        (bool success, ) = payable(escrow.funder).call{value: amount}("");
        require(success, "Refund failed");

        emit EscrowRefunded(bountyId, escrow.funder, amount);
    }

    function getEscrow(uint256 bountyId) external view returns (Escrow memory) {
        Escrow memory escrow = _escrows[bountyId];
        if (escrow.createdAt == 0) {
            revert EscrowNotFound(bountyId);
        }
        return escrow;
    }
}

contracts/BountyRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.25;

/**
 * @title BountyRegistry
 * @notice Tracks legal bounty metadata and cross-chain provenance references for Story, ICP, and Constellation DAG.
 * @dev Designed to be plug-and-play for AI-generated and human-generated submissions that require on-chain proofs.
 */
contract BountyRegistry {
    struct Bounty {
        address creator;
        string metadataUri;
        string storyAssetId;
        string icpCanisterId;
        string dagProofHash;
        bool active;
    }

    struct Submission {
        address submitter;
        string docId;
        string storyAssetId;
        string icpCanisterId;
        string dagProofHash;
        uint256 timestamp;
    }

    mapping(uint256 bountyId => Bounty) private _bounties;
    mapping(uint256 bountyId => Submission[]) private _submissions;

    event BountyCreated(uint256 indexed bountyId, address indexed creator, string metadataUri);
    event BountyStatusUpdated(uint256 indexed bountyId, bool active);
    event BountyProofsLinked(
        uint256 indexed bountyId,
        string storyAssetId,
        string icpCanisterId,
        string dagProofHash
    );
    event SubmissionRegistered(
        uint256 indexed bountyId,
        address indexed submitter,
        string docId,
        string storyAssetId,
        string icpCanisterId,
        string dagProofHash
    );

    error BountyNotFound(uint256 bountyId);
    error BountyNotActive(uint256 bountyId);
    error SubmissionIndexOutOfBounds(uint256 bountyId, uint256 requestedIndex);

    /**
     * @notice Create or overwrite a bounty definition with metadata and activate it.
     * @dev Bounty identifiers are managed off-chain (e.g., Django backend) to align with AI workflows.
     */
    function upsertBounty(uint256 bountyId, string calldata metadataUri, bool active) external {
        Bounty storage bounty = _bounties[bountyId];
        bool isNew = bounty.creator == address(0);
        bounty.creator = msg.sender;
        bounty.metadataUri = metadataUri;
        bounty.active = active;

        if (isNew) {
            emit BountyCreated(bountyId, msg.sender, metadataUri);
        } else {
            emit BountyStatusUpdated(bountyId, active);
        }
    }

    /**
     * @notice Link external proof references for a bounty in bulk.
     * @param bountyId Identifier managed off-chain.
     * @param storyAssetId Story Protocol asset identifier.
     * @param icpCanisterId ICP canister ID where the metadata hash is stored.
     * @param dagProofHash Constellation DAG hash representing agentic reasoning chain.
     */
    function linkBountyProofs(
        uint256 bountyId,
        string calldata storyAssetId,
        string calldata icpCanisterId,
        string calldata dagProofHash
    ) external {
        Bounty storage bounty = _bounties[bountyId];
        if (bounty.creator == address(0)) {
            revert BountyNotFound(bountyId);
        }
        bounty.storyAssetId = storyAssetId;
        bounty.icpCanisterId = icpCanisterId;
        bounty.dagProofHash = dagProofHash;

        emit BountyProofsLinked(bountyId, storyAssetId, icpCanisterId, dagProofHash);
    }

    /**
     * @notice Register a submission with its cross-chain references.
     * @dev Requires the bounty to be active. Off-chain services should ensure docId uniqueness as needed.
     */
    function registerSubmission(
        uint256 bountyId,
        string calldata docId,
        string calldata storyAssetId,
        string calldata icpCanisterId,
        string calldata dagProofHash
    ) external {
        Bounty storage bounty = _bounties[bountyId];
        if (bounty.creator == address(0)) {
            revert BountyNotFound(bountyId);
        }
        if (!bounty.active) {
            revert BountyNotActive(bountyId);
        }

        _submissions[bountyId].push(
            Submission({
                submitter: msg.sender,
                docId: docId,
                storyAssetId: storyAssetId,
                icpCanisterId: icpCanisterId,
                dagProofHash: dagProofHash,
                timestamp: block.timestamp
            })
        );

        emit SubmissionRegistered(
            bountyId,
            msg.sender,
            docId,
            storyAssetId,
            icpCanisterId,
            dagProofHash
        );
    }

    function getBounty(uint256 bountyId) external view returns (Bounty memory) {
        Bounty memory bounty = _bounties[bountyId];
        if (bounty.creator == address(0)) {
            revert BountyNotFound(bountyId);
        }
        return bounty;
    }

    function getSubmissionCount(uint256 bountyId) external view returns (uint256) {
        return _submissions[bountyId].length;
    }

    function getSubmission(
        uint256 bountyId,
        uint256 index
    ) external view returns (Submission memory) {
        if (index >= _submissions[bountyId].length) {
            revert SubmissionIndexOutOfBounds(bountyId, index);
        }
        return _submissions[bountyId][index];
    }
}

contracts/HakiToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.25;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title HakiToken
 * @notice ERC20 token with provenance tagging for Story Protocol, ICP canisters, and Constellation DAG proofs.
 * @dev Mint operations emit hashes tying tokenized legal work to cross-chain records. Supports multi-module minters.
 */
contract HakiToken is ERC20, AccessControl {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    struct Provenance {
        string storyAssetId;
        string icpCanisterId;
        string dagProofHash;
        uint256 mintedAmount;
        uint256 timestamp;
    }

    mapping(bytes32 workflowId => Provenance) private _provenanceByWorkflow;

    event ProvenanceLogged(
        bytes32 indexed workflowId,
        address indexed to,
        uint256 amount,
        string storyAssetId,
        string icpCanisterId,
        string dagProofHash
    );

    constructor(address admin) ERC20("HakiChain Token", "HAKI") {
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(MINTER_ROLE, admin);
    }

    /**
     * @notice Mint HAKI tokens while anchoring provenance references.
     * @param to Recipient of the minted tokens.
     * @param amount Amount of tokens to mint (18 decimal precision).
     * @param storyAssetId Story Protocol asset identifier for the underlying IP.
     * @param icpCanisterId ICP canister storing metadata hashes.
     * @param dagProofHash Constellation DAG hash for agentic reasoning trace.
     * @param workflowTag Arbitrary tag (e.g., AI agent run ID) supplied by off-chain workflow.
     */
    function mintWithProvenance(
        address to,
        uint256 amount,
        string calldata storyAssetId,
        string calldata icpCanisterId,
        string calldata dagProofHash,
        string calldata workflowTag
    ) external onlyRole(MINTER_ROLE) {
        bytes32 workflowId = keccak256(
            abi.encodePacked(workflowTag, storyAssetId, icpCanisterId, dagProofHash)
        );

        _mint(to, amount);

        _provenanceByWorkflow[workflowId] = Provenance({
            storyAssetId: storyAssetId,
            icpCanisterId: icpCanisterId,
            dagProofHash: dagProofHash,
            mintedAmount: amount,
            timestamp: block.timestamp
        });

        emit ProvenanceLogged(workflowId, to, amount, storyAssetId, icpCanisterId, dagProofHash);
    }

    function grantMinterRole(address account) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _grantRole(MINTER_ROLE, account);
    }

    function revokeMinterRole(address account) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _revokeRole(MINTER_ROLE, account);
    }

    function provenanceByWorkflow(bytes32 workflowId) external view returns (Provenance memory) {
        return _provenanceByWorkflow[workflowId];
    }
}

